import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import LoginForm from './components/LoginForm';
import OrdersPage from './pages/OrdersPage';
import CartPage from './pages/CartPage';
import API from './services/api';
import { CartProvider, CartContext } from './CartContext';
import ProtectedRoute from './components/ProtectedRoute';
import './index.css'; // ✅ Tailwind CSS included here

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem('access'));

  const handleLogout = () => {
    localStorage.clear();
    setIsLoggedIn(false);
  };

  return (
    <Router>
      <CartProvider>
        <div className="p-8">
          <h1 className="text-3xl font-bold mb-4">📚 The Book Nook</h1>
          {!isLoggedIn ? (
            <LoginForm onLogin={() => setIsLoggedIn(true)} />
          ) : (
            <>
              <nav className="mb-6 flex gap-4 items-center bg-gray-100 px-4 py-3 rounded-lg shadow">
                <Link to="/" className="text-gray-800 font-semibold hover:underline">🏠 Home</Link>
                <Link to="/orders" className="text-gray-800 font-semibold hover:underline">🧾 Orders</Link>
                <Link to="/cart" className="text-gray-800 font-semibold hover:underline">🛍️ Cart</Link>
                <button
                  onClick={handleLogout}
                  className="ml-auto px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 transition"
                >
                  Logout
                </button>
              </nav>
              <Routes>
                <Route path="/" element={<BookList />} />
                <Route path="/orders" element={
                  <ProtectedRoute><OrdersPage /></ProtectedRoute>
                } />
                <Route path="/cart" element={
                  <ProtectedRoute><CartPage /></ProtectedRoute>
                } />
              </Routes>
            </>
          )}
        </div>
      </CartProvider>
    </Router>
  );
}

// 🧾 BookList Component (with Add to Cart)
function BookList() {
  const [books, setBooks] = useState([]);
  const { addToCart } = React.useContext(CartContext);

  useEffect(() => {
    API.get('/books/')
      .then(res => setBooks(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div>
      <h2 className="text-2xl font-semibold mb-4">📘 Book List</h2>
      <ul className="space-y-3">
        {books.map(book => (
          <li key={book.id} className="p-3 bg-white shadow rounded flex justify-between items-center">
            <div>
              <strong>{book.title}</strong> by {book.author.name} - {book.price} Ks
            </div>
            <button
              onClick={() => addToCart(book)}
              className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600 transition"
            >
              ➕ Add to Cart
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
